import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
  Users, Clock, FileText, Download, Plus, X, 
  Upload, Calendar, Filter, ChevronDown, Trash2,
  Eye, BarChart3, TrendingUp
} from 'lucide-react';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function App() {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [employees, setEmployees] = useState([]);
  const [reports, setReports] = useState([]);
  const [stats, setStats] = useState({});
  const [loading, setLoading] = useState(false);

  // Modals
  const [showEmployeeModal, setShowEmployeeModal] = useState(false);
  const [showReportModal, setShowReportModal] = useState(false);
  const [selectedReport, setSelectedReport] = useState(null);

  // Filters
  const [filters, setFilters] = useState({
    employee_id: '',
    start_date: '',
    end_date: ''
  });

  // Forms
  const [employeeForm, setEmployeeForm] = useState({ name: '', email: '', role: '' });
  const [reportForm, setReportForm] = useState({
    employee_id: '',
    date: new Date().toISOString().split('T')[0],
    hours: '',
    description: '',
    screenshots: []
  });

  useEffect(() => {
    fetchEmployees();
    fetchReports();
    fetchStats();
  }, []);

  useEffect(() => {
    fetchReports();
  }, [filters]);

  const fetchEmployees = async () => {
    try {
      const response = await axios.get(`${API_URL}/employees`);
      setEmployees(response.data);
    } catch (error) {
      console.error('Error fetching employees:', error);
    }
  };

  const fetchReports = async () => {
    setLoading(true);
    try {
      const params = new URLSearchParams();
      if (filters.employee_id) params.append('employee_id', filters.employee_id);
      if (filters.start_date) params.append('start_date', filters.start_date);
      if (filters.end_date) params.append('end_date', filters.end_date);

      const response = await axios.get(`${API_URL}/reports?${params}`);
      setReports(response.data);
    } catch (error) {
      console.error('Error fetching reports:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await axios.get(`${API_URL}/stats`);
      setStats(response.data);
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  const handleAddEmployee = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${API_URL}/employees`, employeeForm);
      setEmployeeForm({ name: '', email: '', role: '' });
      setShowEmployeeModal(false);
      fetchEmployees();
      fetchStats();
    } catch (error) {
      alert(error.response?.data?.error || 'Error adding employee');
    }
  };

  const handleDeleteEmployee = async (id) => {
    if (!window.confirm('Are you sure you want to delete this employee?')) return;
    try {
      await axios.delete(`${API_URL}/employees/${id}`);
      fetchEmployees();
      fetchStats();
    } catch (error) {
      alert('Error deleting employee');
    }
  };

  const handleAddReport = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('employee_id', reportForm.employee_id);
    formData.append('date', reportForm.date);
    formData.append('hours', reportForm.hours);
    formData.append('description', reportForm.description);

    reportForm.screenshots.forEach(file => {
      formData.append('screenshots', file);
    });

    try {
      await axios.post(`${API_URL}/reports`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      setReportForm({
        employee_id: '',
        date: new Date().toISOString().split('T')[0],
        hours: '',
        description: '',
        screenshots: []
      });
      setShowReportModal(false);
      fetchReports();
      fetchStats();
    } catch (error) {
      alert(error.response?.data?.error || 'Error submitting report');
    }
  };

  const handleDeleteReport = async (id) => {
    if (!window.confirm('Are you sure you want to delete this report?')) return;
    try {
      await axios.delete(`${API_URL}/reports/${id}`);
      fetchReports();
      fetchStats();
    } catch (error) {
      alert('Error deleting report');
    }
  };

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    setReportForm({ ...reportForm, screenshots: files });
  };

  const handleExportCSV = () => {
    const params = new URLSearchParams();
    if (filters.employee_id) params.append('employee_id', filters.employee_id);
    if (filters.start_date) params.append('start_date', filters.start_date);
    if (filters.end_date) params.append('end_date', filters.end_date);

    window.open(`${API_URL}/reports/export/csv?${params}`, '_blank');
  };

  const clearFilters = () => {
    setFilters({ employee_id: '', start_date: '', end_date: '' });
  };

  return (
    <div className="app">
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Outfit:wght@300;400;500;600;700;800&family=JetBrains+Mono:wght@400;600&display=swap');

        * {
          margin: 0;
          padding: 0;
          box-sizing: border-box;
        }

        body {
          font-family: 'Outfit', sans-serif;
          background: linear-gradient(135deg, #0a0e27 0%, #1a1f3a 100%);
          color: #e8eaf6;
          min-height: 100vh;
        }

        .app {
          min-height: 100vh;
          animation: fadeIn 0.6s ease-out;
        }

        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }

        .header {
          background: rgba(15, 20, 40, 0.8);
          backdrop-filter: blur(20px);
          border-bottom: 1px solid rgba(255, 255, 255, 0.05);
          padding: 1.5rem 2rem;
          position: sticky;
          top: 0;
          z-index: 100;
          animation: slideDown 0.5s ease-out;
        }

        @keyframes slideDown {
          from { transform: translateY(-100%); }
          to { transform: translateY(0); }
        }

        .header-content {
          max-width: 1400px;
          margin: 0 auto;
          display: flex;
          justify-content: space-between;
          align-items: center;
        }

        .logo {
          display: flex;
          align-items: center;
          gap: 0.75rem;
          font-size: 1.75rem;
          font-weight: 800;
          letter-spacing: -0.02em;
        }

        .logo-gradient {
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
        }

        .nav-tabs {
          display: flex;
          gap: 0.5rem;
        }

        .nav-tab {
          padding: 0.75rem 1.5rem;
          background: transparent;
          border: none;
          color: #a5b4fc;
          cursor: pointer;
          font-size: 0.95rem;
          font-weight: 500;
          border-radius: 12px;
          transition: all 0.3s ease;
          display: flex;
          align-items: center;
          gap: 0.5rem;
          font-family: 'Outfit', sans-serif;
        }

        .nav-tab:hover {
          background: rgba(102, 126, 234, 0.1);
          color: #c7d2fe;
        }

        .nav-tab.active {
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          color: white;
          box-shadow: 0 4px 20px rgba(102, 126, 234, 0.3);
        }

        .container {
          max-width: 1400px;
          margin: 0 auto;
          padding: 2rem;
        }

        .stats-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
          gap: 1.5rem;
          margin-bottom: 2rem;
          animation: slideUp 0.6s ease-out 0.2s backwards;
        }

        @keyframes slideUp {
          from { 
            opacity: 0;
            transform: translateY(30px);
          }
          to { 
            opacity: 1;
            transform: translateY(0);
          }
        }

        .stat-card {
          background: rgba(30, 35, 60, 0.6);
          backdrop-filter: blur(10px);
          border: 1px solid rgba(255, 255, 255, 0.05);
          border-radius: 20px;
          padding: 1.75rem;
          transition: all 0.3s ease;
        }

        .stat-card:hover {
          transform: translateY(-5px);
          border-color: rgba(102, 126, 234, 0.3);
          box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
        }

        .stat-header {
          display: flex;
          justify-content: space-between;
          align-items: flex-start;
          margin-bottom: 1rem;
        }

        .stat-icon {
          width: 48px;
          height: 48px;
          border-radius: 14px;
          display: flex;
          align-items: center;
          justify-content: center;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
        }

        .stat-label {
          font-size: 0.9rem;
          color: #a5b4fc;
          font-weight: 500;
          text-transform: uppercase;
          letter-spacing: 0.05em;
        }

        .stat-value {
          font-size: 2.5rem;
          font-weight: 800;
          color: #fff;
          line-height: 1;
          font-family: 'JetBrains Mono', monospace;
        }

        .section-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 1.5rem;
          animation: slideUp 0.6s ease-out 0.3s backwards;
        }

        .section-title {
          font-size: 1.75rem;
          font-weight: 700;
          color: #fff;
        }

        .btn {
          padding: 0.75rem 1.5rem;
          border: none;
          border-radius: 12px;
          font-weight: 600;
          cursor: pointer;
          font-size: 0.95rem;
          display: flex;
          align-items: center;
          gap: 0.5rem;
          transition: all 0.3s ease;
          font-family: 'Outfit', sans-serif;
        }

        .btn-primary {
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          color: white;
          box-shadow: 0 4px 15px rgba(102, 126, 234, 0.3);
        }

        .btn-primary:hover {
          transform: translateY(-2px);
          box-shadow: 0 6px 25px rgba(102, 126, 234, 0.4);
        }

        .btn-secondary {
          background: rgba(255, 255, 255, 0.05);
          color: #a5b4fc;
          border: 1px solid rgba(255, 255, 255, 0.1);
        }

        .btn-secondary:hover {
          background: rgba(255, 255, 255, 0.1);
          color: #c7d2fe;
        }

        .btn-danger {
          background: rgba(239, 68, 68, 0.1);
          color: #fca5a5;
          border: 1px solid rgba(239, 68, 68, 0.2);
        }

        .btn-danger:hover {
          background: rgba(239, 68, 68, 0.2);
          color: #fecaca;
        }

        .filters {
          background: rgba(30, 35, 60, 0.6);
          backdrop-filter: blur(10px);
          border: 1px solid rgba(255, 255, 255, 0.05);
          border-radius: 16px;
          padding: 1.5rem;
          margin-bottom: 1.5rem;
          animation: slideUp 0.6s ease-out 0.4s backwards;
        }

        .filters-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
          gap: 1rem;
          margin-bottom: 1rem;
        }

        .filter-group {
          display: flex;
          flex-direction: column;
          gap: 0.5rem;
        }

        .filter-label {
          font-size: 0.85rem;
          color: #a5b4fc;
          font-weight: 500;
          text-transform: uppercase;
          letter-spacing: 0.05em;
        }

        .filter-input, .filter-select {
          padding: 0.75rem 1rem;
          background: rgba(15, 20, 40, 0.8);
          border: 1px solid rgba(255, 255, 255, 0.1);
          border-radius: 10px;
          color: #e8eaf6;
          font-size: 0.95rem;
          font-family: 'Outfit', sans-serif;
          transition: all 0.3s ease;
        }

        .filter-input:focus, .filter-select:focus {
          outline: none;
          border-color: #667eea;
          box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
        }

        .cards-grid {
          display: grid;
          gap: 1.5rem;
          animation: slideUp 0.6s ease-out 0.5s backwards;
        }

        .card {
          background: rgba(30, 35, 60, 0.6);
          backdrop-filter: blur(10px);
          border: 1px solid rgba(255, 255, 255, 0.05);
          border-radius: 16px;
          padding: 1.5rem;
          transition: all 0.3s ease;
        }

        .card:hover {
          border-color: rgba(102, 126, 234, 0.2);
          box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
        }

        .card-header {
          display: flex;
          justify-content: space-between;
          align-items: flex-start;
          margin-bottom: 1rem;
        }

        .card-title {
          font-size: 1.25rem;
          font-weight: 700;
          color: #fff;
          margin-bottom: 0.25rem;
        }

        .card-subtitle {
          font-size: 0.9rem;
          color: #a5b4fc;
        }

        .card-actions {
          display: flex;
          gap: 0.5rem;
        }

        .icon-btn {
          width: 36px;
          height: 36px;
          border: none;
          border-radius: 10px;
          background: rgba(255, 255, 255, 0.05);
          color: #a5b4fc;
          cursor: pointer;
          display: flex;
          align-items: center;
          justify-content: center;
          transition: all 0.3s ease;
        }

        .icon-btn:hover {
          background: rgba(102, 126, 234, 0.2);
          color: #c7d2fe;
        }

        .icon-btn.danger:hover {
          background: rgba(239, 68, 68, 0.2);
          color: #fca5a5;
        }

        .report-details {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
          gap: 1rem;
          margin-bottom: 1rem;
        }

        .detail-item {
          display: flex;
          flex-direction: column;
          gap: 0.25rem;
        }

        .detail-label {
          font-size: 0.8rem;
          color: #a5b4fc;
          text-transform: uppercase;
          letter-spacing: 0.05em;
        }

        .detail-value {
          font-size: 1.1rem;
          color: #fff;
          font-weight: 600;
          font-family: 'JetBrains Mono', monospace;
        }

        .screenshots-grid {
          display: grid;
          grid-template-columns: repeat(auto-fill, minmax(120px, 1fr));
          gap: 0.75rem;
          margin-top: 1rem;
        }

        .screenshot-thumb {
          width: 100%;
          height: 100px;
          object-fit: cover;
          border-radius: 10px;
          border: 2px solid rgba(255, 255, 255, 0.1);
          cursor: pointer;
          transition: all 0.3s ease;
        }

        .screenshot-thumb:hover {
          border-color: #667eea;
          transform: scale(1.05);
        }

        .modal-overlay {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          background: rgba(0, 0, 0, 0.8);
          backdrop-filter: blur(8px);
          display: flex;
          align-items: center;
          justify-content: center;
          z-index: 1000;
          animation: fadeIn 0.3s ease-out;
        }

        .modal {
          background: rgba(20, 25, 45, 0.95);
          border: 1px solid rgba(255, 255, 255, 0.1);
          border-radius: 24px;
          padding: 2rem;
          max-width: 600px;
          width: 90%;
          max-height: 90vh;
          overflow-y: auto;
          animation: scaleIn 0.3s ease-out;
        }

        @keyframes scaleIn {
          from {
            opacity: 0;
            transform: scale(0.9);
          }
          to {
            opacity: 1;
            transform: scale(1);
          }
        }

        .modal-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 1.5rem;
        }

        .modal-title {
          font-size: 1.5rem;
          font-weight: 700;
          color: #fff;
        }

        .close-btn {
          width: 36px;
          height: 36px;
          border: none;
          border-radius: 10px;
          background: rgba(255, 255, 255, 0.05);
          color: #a5b4fc;
          cursor: pointer;
          display: flex;
          align-items: center;
          justify-content: center;
          transition: all 0.3s ease;
        }

        .close-btn:hover {
          background: rgba(239, 68, 68, 0.2);
          color: #fca5a5;
        }

        .form {
          display: flex;
          flex-direction: column;
          gap: 1.25rem;
        }

        .form-group {
          display: flex;
          flex-direction: column;
          gap: 0.5rem;
        }

        .form-label {
          font-size: 0.9rem;
          color: #a5b4fc;
          font-weight: 500;
        }

        .form-input, .form-select, .form-textarea {
          padding: 0.875rem 1rem;
          background: rgba(15, 20, 40, 0.8);
          border: 1px solid rgba(255, 255, 255, 0.1);
          border-radius: 12px;
          color: #e8eaf6;
          font-size: 1rem;
          font-family: 'Outfit', sans-serif;
          transition: all 0.3s ease;
        }

        .form-textarea {
          min-height: 100px;
          resize: vertical;
        }

        .form-input:focus, .form-select:focus, .form-textarea:focus {
          outline: none;
          border-color: #667eea;
          box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
        }

        .file-upload {
          padding: 2rem;
          border: 2px dashed rgba(255, 255, 255, 0.2);
          border-radius: 12px;
          text-align: center;
          cursor: pointer;
          transition: all 0.3s ease;
          background: rgba(15, 20, 40, 0.5);
        }

        .file-upload:hover {
          border-color: #667eea;
          background: rgba(102, 126, 234, 0.05);
        }

        .file-upload-input {
          display: none;
        }

        .file-list {
          margin-top: 0.75rem;
          font-size: 0.9rem;
          color: #a5b4fc;
        }

        .empty-state {
          text-align: center;
          padding: 4rem 2rem;
          color: #a5b4fc;
        }

        .empty-icon {
          width: 64px;
          height: 64px;
          margin: 0 auto 1rem;
          opacity: 0.3;
        }

        .loading {
          text-align: center;
          padding: 2rem;
          color: #a5b4fc;
        }

        .employees-grid {
          display: grid;
          grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
          gap: 1.5rem;
        }

        @media (max-width: 768px) {
          .header-content {
            flex-direction: column;
            gap: 1rem;
          }

          .nav-tabs {
            width: 100%;
            justify-content: space-between;
          }

          .nav-tab {
            flex: 1;
            justify-content: center;
            padding: 0.75rem 0.5rem;
            font-size: 0.85rem;
          }

          .stat-value {
            font-size: 2rem;
          }

          .filters-grid {
            grid-template-columns: 1fr;
          }
        }
      `}</style>

      {/* Header */}
      <header className="header">
        <div className="header-content">
          <div className="logo">
            <BarChart3 size={32} />
            <span className="logo-gradient">EOD Monitor</span>
          </div>
          <nav className="nav-tabs">
            <button 
              className={`nav-tab ${activeTab === 'dashboard' ? 'active' : ''}`}
              onClick={() => setActiveTab('dashboard')}
            >
              <BarChart3 size={18} />
              Dashboard
            </button>
            <button 
              className={`nav-tab ${activeTab === 'reports' ? 'active' : ''}`}
              onClick={() => setActiveTab('reports')}
            >
              <FileText size={18} />
              Reports
            </button>
            <button 
              className={`nav-tab ${activeTab === 'employees' ? 'active' : ''}`}
              onClick={() => setActiveTab('employees')}
            >
              <Users size={18} />
              Employees
            </button>
          </nav>
        </div>
      </header>

      <div className="container">
        {/* Dashboard Tab */}
        {activeTab === 'dashboard' && (
          <>
            <div className="stats-grid">
              <div className="stat-card">
                <div className="stat-header">
                  <div>
                    <div className="stat-label">Total Employees</div>
                    <div className="stat-value">{stats.totalEmployees || 0}</div>
                  </div>
                  <div className="stat-icon">
                    <Users size={24} color="white" />
                  </div>
                </div>
              </div>

              <div className="stat-card">
                <div className="stat-header">
                  <div>
                    <div className="stat-label">Total Reports</div>
                    <div className="stat-value">{stats.totalReports || 0}</div>
                  </div>
                  <div className="stat-icon">
                    <FileText size={24} color="white" />
                  </div>
                </div>
              </div>

              <div className="stat-card">
                <div className="stat-header">
                  <div>
                    <div className="stat-label">Total Hours</div>
                    <div className="stat-value">{stats.totalHours || 0}</div>
                  </div>
                  <div className="stat-icon">
                    <Clock size={24} color="white" />
                  </div>
                </div>
              </div>

              <div className="stat-card">
                <div className="stat-header">
                  <div>
                    <div className="stat-label">Today's Reports</div>
                    <div className="stat-value">{stats.reportsToday || 0}</div>
                  </div>
                  <div className="stat-icon">
                    <TrendingUp size={24} color="white" />
                  </div>
                </div>
              </div>
            </div>

            <div className="section-header">
              <h2 className="section-title">Recent EOD Reports</h2>
              <button className="btn btn-primary" onClick={() => setShowReportModal(true)}>
                <Plus size={18} />
                New Report
              </button>
            </div>

            {reports.slice(0, 5).length === 0 ? (
              <div className="empty-state">
                <FileText size={64} className="empty-icon" />
                <p>No reports yet. Create your first report!</p>
              </div>
            ) : (
              <div className="cards-grid">
                {reports.slice(0, 5).map(report => (
                  <div key={report.id} className="card">
                    <div className="card-header">
                      <div>
                        <div className="card-title">{report.employee_name}</div>
                        <div className="card-subtitle">{report.employee_role}</div>
                      </div>
                      <div className="card-actions">
                        <button 
                          className="icon-btn"
                          onClick={() => setSelectedReport(report)}
                        >
                          <Eye size={18} />
                        </button>
                        <button 
                          className="icon-btn danger"
                          onClick={() => handleDeleteReport(report.id)}
                        >
                          <Trash2 size={18} />
                        </button>
                      </div>
                    </div>
                    <div className="report-details">
                      <div className="detail-item">
                        <div className="detail-label">Date</div>
                        <div className="detail-value">{report.date}</div>
                      </div>
                      <div className="detail-item">
                        <div className="detail-label">Hours</div>
                        <div className="detail-value">{report.hours}h</div>
                      </div>
                      <div className="detail-item">
                        <div className="detail-label">Screenshots</div>
                        <div className="detail-value">{report.screenshots?.length || 0}</div>
                      </div>
                    </div>
                    {report.description && (
                      <p style={{ marginTop: '1rem', color: '#a5b4fc', fontSize: '0.95rem' }}>
                        {report.description}
                      </p>
                    )}
                  </div>
                ))}
              </div>
            )}
          </>
        )}

        {/* Reports Tab */}
        {activeTab === 'reports' && (
          <>
            <div className="filters">
              <div className="filters-grid">
                <div className="filter-group">
                  <label className="filter-label">Employee</label>
                  <select 
                    className="filter-select"
                    value={filters.employee_id}
                    onChange={(e) => setFilters({...filters, employee_id: e.target.value})}
                  >
                    <option value="">All Employees</option>
                    {employees.map(emp => (
                      <option key={emp.id} value={emp.id}>{emp.name}</option>
                    ))}
                  </select>
                </div>

                <div className="filter-group">
                  <label className="filter-label">Start Date</label>
                  <input 
                    type="date"
                    className="filter-input"
                    value={filters.start_date}
                    onChange={(e) => setFilters({...filters, start_date: e.target.value})}
                  />
                </div>

                <div className="filter-group">
                  <label className="filter-label">End Date</label>
                  <input 
                    type="date"
                    className="filter-input"
                    value={filters.end_date}
                    onChange={(e) => setFilters({...filters, end_date: e.target.value})}
                  />
                </div>
              </div>

              <div style={{ display: 'flex', gap: '0.75rem', marginTop: '1rem' }}>
                <button className="btn btn-secondary" onClick={clearFilters}>
                  <X size={18} />
                  Clear Filters
                </button>
                <button className="btn btn-secondary" onClick={handleExportCSV}>
                  <Download size={18} />
                  Export CSV
                </button>
              </div>
            </div>

            <div className="section-header">
              <h2 className="section-title">All Reports ({reports.length})</h2>
              <button className="btn btn-primary" onClick={() => setShowReportModal(true)}>
                <Plus size={18} />
                New Report
              </button>
            </div>

            {loading ? (
              <div className="loading">Loading reports...</div>
            ) : reports.length === 0 ? (
              <div className="empty-state">
                <FileText size={64} className="empty-icon" />
                <p>No reports found. Adjust filters or create a new report.</p>
              </div>
            ) : (
              <div className="cards-grid">
                {reports.map(report => (
                  <div key={report.id} className="card">
                    <div className="card-header">
                      <div>
                        <div className="card-title">{report.employee_name}</div>
                        <div className="card-subtitle">{report.employee_role}</div>
                      </div>
                      <div className="card-actions">
                        <button 
                          className="icon-btn"
                          onClick={() => setSelectedReport(report)}
                        >
                          <Eye size={18} />
                        </button>
                        <button 
                          className="icon-btn danger"
                          onClick={() => handleDeleteReport(report.id)}
                        >
                          <Trash2 size={18} />
                        </button>
                      </div>
                    </div>
                    <div className="report-details">
                      <div className="detail-item">
                        <div className="detail-label">Date</div>
                        <div className="detail-value">{report.date}</div>
                      </div>
                      <div className="detail-item">
                        <div className="detail-label">Hours</div>
                        <div className="detail-value">{report.hours}h</div>
                      </div>
                      <div className="detail-item">
                        <div className="detail-label">Screenshots</div>
                        <div className="detail-value">{report.screenshots?.length || 0}</div>
                      </div>
                    </div>
                    {report.description && (
                      <p style={{ marginTop: '1rem', color: '#a5b4fc', fontSize: '0.95rem' }}>
                        {report.description}
                      </p>
                    )}
                    {report.screenshots && report.screenshots.length > 0 && (
                      <div className="screenshots-grid">
                        {report.screenshots.map(screenshot => (
                          <img 
                            key={screenshot.id}
                            src={`${API_URL.replace('/api', '')}/uploads/${screenshot.filepath}`}
                            alt={screenshot.filename}
                            className="screenshot-thumb"
                            onClick={() => window.open(`${API_URL.replace('/api', '')}/uploads/${screenshot.filepath}`, '_blank')}
                          />
                        ))}
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </>
        )}

        {/* Employees Tab */}
        {activeTab === 'employees' && (
          <>
            <div className="section-header">
              <h2 className="section-title">Team Members ({employees.length})</h2>
              <button className="btn btn-primary" onClick={() => setShowEmployeeModal(true)}>
                <Plus size={18} />
                Add Employee
              </button>
            </div>

            {employees.length === 0 ? (
              <div className="empty-state">
                <Users size={64} className="empty-icon" />
                <p>No employees yet. Add your first team member!</p>
              </div>
            ) : (
              <div className="employees-grid">
                {employees.map(employee => (
                  <div key={employee.id} className="card">
                    <div className="card-header">
                      <div>
                        <div className="card-title">{employee.name}</div>
                        <div className="card-subtitle">{employee.role}</div>
                      </div>
                      <button 
                        className="icon-btn danger"
                        onClick={() => handleDeleteEmployee(employee.id)}
                      >
                        <Trash2 size={18} />
                      </button>
                    </div>
                    <div style={{ marginTop: '1rem' }}>
                      <div className="detail-label">Email</div>
                      <div style={{ color: '#c7d2fe', marginTop: '0.25rem' }}>{employee.email}</div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </>
        )}
      </div>

      {/* Add Employee Modal */}
      {showEmployeeModal && (
        <div className="modal-overlay" onClick={() => setShowEmployeeModal(false)}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3 className="modal-title">Add New Employee</h3>
              <button className="close-btn" onClick={() => setShowEmployeeModal(false)}>
                <X size={20} />
              </button>
            </div>
            <form className="form" onSubmit={handleAddEmployee}>
              <div className="form-group">
                <label className="form-label">Full Name *</label>
                <input 
                  type="text"
                  className="form-input"
                  value={employeeForm.name}
                  onChange={(e) => setEmployeeForm({...employeeForm, name: e.target.value})}
                  required
                  placeholder="John Doe"
                />
              </div>

              <div className="form-group">
                <label className="form-label">Email *</label>
                <input 
                  type="email"
                  className="form-input"
                  value={employeeForm.email}
                  onChange={(e) => setEmployeeForm({...employeeForm, email: e.target.value})}
                  required
                  placeholder="john@example.com"
                />
              </div>

              <div className="form-group">
                <label className="form-label">Role *</label>
                <input 
                  type="text"
                  className="form-input"
                  value={employeeForm.role}
                  onChange={(e) => setEmployeeForm({...employeeForm, role: e.target.value})}
                  required
                  placeholder="Software Engineer"
                />
              </div>

              <button type="submit" className="btn btn-primary" style={{ width: '100%' }}>
                <Plus size={18} />
                Add Employee
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Add Report Modal */}
      {showReportModal && (
        <div className="modal-overlay" onClick={() => setShowReportModal(false)}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3 className="modal-title">Submit EOD Report</h3>
              <button className="close-btn" onClick={() => setShowReportModal(false)}>
                <X size={20} />
              </button>
            </div>
            <form className="form" onSubmit={handleAddReport}>
              <div className="form-group">
                <label className="form-label">Employee *</label>
                <select 
                  className="form-select"
                  value={reportForm.employee_id}
                  onChange={(e) => setReportForm({...reportForm, employee_id: e.target.value})}
                  required
                >
                  <option value="">Select Employee</option>
                  {employees.map(emp => (
                    <option key={emp.id} value={emp.id}>{emp.name} - {emp.role}</option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label className="form-label">Date *</label>
                <input 
                  type="date"
                  className="form-input"
                  value={reportForm.date}
                  onChange={(e) => setReportForm({...reportForm, date: e.target.value})}
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Hours Worked *</label>
                <input 
                  type="number"
                  step="0.5"
                  min="0"
                  max="24"
                  className="form-input"
                  value={reportForm.hours}
                  onChange={(e) => setReportForm({...reportForm, hours: e.target.value})}
                  required
                  placeholder="8"
                />
              </div>

              <div className="form-group">
                <label className="form-label">Description</label>
                <textarea 
                  className="form-textarea"
                  value={reportForm.description}
                  onChange={(e) => setReportForm({...reportForm, description: e.target.value})}
                  placeholder="What did you work on today?"
                />
              </div>

              <div className="form-group">
                <label className="form-label">Screenshots</label>
                <label className="file-upload">
                  <input 
                    type="file"
                    className="file-upload-input"
                    accept="image/*"
                    multiple
                    onChange={handleFileChange}
                  />
                  <Upload size={32} style={{ margin: '0 auto 0.5rem', opacity: 0.5 }} />
                  <div style={{ color: '#a5b4fc', marginBottom: '0.25rem' }}>
                    Click to upload screenshots
                  </div>
                  <div style={{ fontSize: '0.85rem', opacity: 0.7 }}>
                    PNG, JPG, GIF up to 10MB
                  </div>
                  {reportForm.screenshots.length > 0 && (
                    <div className="file-list">
                      {reportForm.screenshots.length} file(s) selected
                    </div>
                  )}
                </label>
              </div>

              <button type="submit" className="btn btn-primary" style={{ width: '100%' }}>
                <FileText size={18} />
                Submit Report
              </button>
            </form>
          </div>
        </div>
      )}

      {/* View Report Modal */}
      {selectedReport && (
        <div className="modal-overlay" onClick={() => setSelectedReport(null)}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3 className="modal-title">Report Details</h3>
              <button className="close-btn" onClick={() => setSelectedReport(null)}>
                <X size={20} />
              </button>
            </div>
            <div style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
              <div>
                <div className="detail-label">Employee</div>
                <div className="card-title" style={{ marginTop: '0.5rem' }}>
                  {selectedReport.employee_name}
                </div>
                <div className="card-subtitle">{selectedReport.employee_role}</div>
              </div>

              <div className="report-details">
                <div className="detail-item">
                  <div className="detail-label">Date</div>
                  <div className="detail-value">{selectedReport.date}</div>
                </div>
                <div className="detail-item">
                  <div className="detail-label">Hours</div>
                  <div className="detail-value">{selectedReport.hours}h</div>
                </div>
              </div>

              {selectedReport.description && (
                <div>
                  <div className="detail-label">Description</div>
                  <p style={{ color: '#c7d2fe', marginTop: '0.5rem', lineHeight: '1.6' }}>
                    {selectedReport.description}
                  </p>
                </div>
              )}

              {selectedReport.screenshots && selectedReport.screenshots.length > 0 && (
                <div>
                  <div className="detail-label">Screenshots ({selectedReport.screenshots.length})</div>
                  <div className="screenshots-grid" style={{ marginTop: '0.75rem' }}>
                    {selectedReport.screenshots.map(screenshot => (
                      <img 
                        key={screenshot.id}
                        src={`${API_URL.replace('/api', '')}/uploads/${screenshot.filepath}`}
                        alt={screenshot.filename}
                        className="screenshot-thumb"
                        onClick={() => window.open(`${API_URL.replace('/api', '')}/uploads/${screenshot.filepath}`, '_blank')}
                      />
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
